import React, { Component } from "react";
import ReactDOM from "react-dom";

import "./App.css";
class Forms extends Component{
  constructor(props){
    super(props);
    this.state = {value:localStorage.getItem("nom"), values:localStorage.getItem("sujet")}
    this.handleChangeNom = this.handleChangeNom.bind(this);
    this.handleChangeSujet = this.handleChangeSujet.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChangeNom(event){
    this.setState({value:event.target.value});
    localStorage.setItem("nom",this.state.value);
  }
  handleChangeSujet(event){
    this.setState({values:event.target.values});
    localStorage.setItem("sujet",this.state.values);
  }
  handleSubmit(event) {
    //alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }
  render(){
    return(
      <div className="App">
      <form onSubmit={this.handleSubmit}>
        <label>nom</label>
        <input type="text" value={this.state.value} onChange={this.handleChangeNom} />
        <label>
        Essay:</label>
          <textarea value={this.state.values} onChange={this.handleChangeSujet} />


        <label>choix</label>
        <input type="checkbox" value="ok" />
        <label>troc</label>
        <input type="radio" value="ok" />
        <input type="submit" value="Envoyer" />
      </form>
      <Showtext/>
      </div>
    );
  }


}
class Showtext extends Component{
  render(){
    return(
      <div>vous avez saisi {this.props.value}</div>
    )
  }
}
const rootElement = document.getElementById("root");
ReactDOM.render(<Forms />, rootElement);
